def adjust_gamma(col, fac, highest):
    Gamma = 2.2
    if col == 0.0:
        result = 0
    else:
        result = pow(col * fac, Gamma) * highest
        if result < 0:
            result = 0
        if result > highest:
            result = highest

    return result


